<?php/** * Functions and definitions * * @package WP-Mobili * @subpackage hello-mobili * @since 1.0.0 */// This theme requires WordPress 5.3 or later.if ( version_compare( $GLOBALS['wp_version'], '5.3', '<' ) ) {    require get_template_directory() . '/includes/hooks/back-compat.php';}// Require hook slices.require_once get_template_directory() . '/includes/bootstrap.php';if ( ! function_exists( 'hello_mobili_one_setup' ) ) {    /**     * Sets up theme defaults and registers support for various WordPress features.     *     * Note that this function is hooked into the after_setup_theme hook, which     * runs before the init hook. The init hook is too late for some features, such     * as indicating support for post thumbnails.     *     * @since 1.0.0     *     * @return void     */    function hello_mobili_one_setup() {        /*         * Make theme available for translation.         * Translations can be filed in the /languages/ directory.         * to change 'hello-mobili' to the name of your theme in all the template files.         */        load_theme_textdomain( 'hello-mobili', get_template_directory() . '/languages' );        // Add default posts and comments RSS feed links to head.        add_theme_support( 'automatic-feed-links' );        /*         * Let WordPress manage the document title.         * This theme does not use a hard-coded <title> tag in the document head,         * WordPress will provide it for us.         */        add_theme_support( 'title-tag' );        /**         * Add post-formats support.         */        add_theme_support(            'post-formats',            array(                'link',                'aside',                'gallery',                'image',                'quote',                'status',                'video',                'audio',                'chat',            )        );        /*         * Enable support for Post Thumbnails on posts and pages.         *         * @link https://developer.wordpress.org/themes/functionality/featured-images-post-thumbnails/         */        add_theme_support( 'post-thumbnails' );        set_post_thumbnail_size( 1568, 9999 );        register_nav_menus(            array(                'primary' => esc_html__( 'Primary menu', 'hello-mobili' )            )        );        /*         * Switch default core markup for search form, comment form, and comments         * to output valid HTML5.         */        add_theme_support(            'html5',            array(                'comment-form',                'comment-list',                'gallery',                'caption',                'style',                'script',                'navigation-widgets',            )        );        /*         * Add support for core custom logo.         *         * @link https://codex.wordpress.org/Theme_Logo         */        $logo_width  = 300;        $logo_height = 100;        add_theme_support(            'custom-logo',            array(                'height'               => $logo_height,                'width'                => $logo_width,                'flex-width'           => true,                'flex-height'          => true,                'unlink-homepage-logo' => true,            )        );        // Add theme support for selective refresh for widgets.        add_theme_support( 'customize-selective-refresh-widgets' );        // Add support for Block Styles.        add_theme_support( 'wp-block-styles' );        // Add support for full and wide align images.        add_theme_support( 'align-wide' );        // Add support for editor styles.        add_theme_support( 'editor-styles' );        $editor_stylesheet_path = './assets/styles/style-editor.css';        // Enqueue editor styles.        add_editor_style( $editor_stylesheet_path );        // Add custom editor font sizes.        add_theme_support(            'editor-font-sizes',            array(                array(                    'name'      => esc_html__( 'Extra small', 'hello-mobili' ),                    'shortName' => esc_html_x( 'XS', 'Font size', 'hello-mobili' ),                    'size'      => 16,                    'slug'      => 'extra-small',                ),                array(                    'name'      => esc_html__( 'Small', 'hello-mobili' ),                    'shortName' => esc_html_x( 'S', 'Font size', 'hello-mobili' ),                    'size'      => 18,                    'slug'      => 'small',                ),                array(                    'name'      => esc_html__( 'Normal', 'hello-mobili' ),                    'shortName' => esc_html_x( 'M', 'Font size', 'hello-mobili' ),                    'size'      => 20,                    'slug'      => 'normal',                ),                array(                    'name'      => esc_html__( 'Large', 'hello-mobili' ),                    'shortName' => esc_html_x( 'L', 'Font size', 'hello-mobili' ),                    'size'      => 24,                    'slug'      => 'large',                ),                array(                    'name'      => esc_html__( 'Extra large', 'hello-mobili' ),                    'shortName' => esc_html_x( 'XL', 'Font size', 'hello-mobili' ),                    'size'      => 40,                    'slug'      => 'extra-large',                ),                array(                    'name'      => esc_html__( 'Huge', 'hello-mobili' ),                    'shortName' => esc_html_x( 'XXL', 'Font size', 'hello-mobili' ),                    'size'      => 96,                    'slug'      => 'huge',                ),                array(                    'name'      => esc_html__( 'Gigantic', 'hello-mobili' ),                    'shortName' => esc_html_x( 'XXXL', 'Font size', 'hello-mobili' ),                    'size'      => 144,                    'slug'      => 'gigantic',                ),            )        );        // Custom background color.        add_theme_support(            'custom-background',            array(                'default-color' => 'ffffff',            )        );        // Editor color palette.        $black     = '#000000';        $dark_gray = '#28303D';        $gray      = '#39414D';        $green     = '#D1E4DD';        $blue      = '#D1DFE4';        $purple    = '#D1D1E4';        $red       = '#E4D1D1';        $orange    = '#ff8d00';        $yellow    = '#EEEADD';        $white     = '#FFFFFF';        add_theme_support(            'editor-color-palette',            array(                array(                    'name'  => esc_html__( 'Black', 'hello-mobili' ),                    'slug'  => 'black',                    'color' => $black,                ),                array(                    'name'  => esc_html__( 'Dark gray', 'hello-mobili' ),                    'slug'  => 'dark-gray',                    'color' => $dark_gray,                ),                array(                    'name'  => esc_html__( 'Gray', 'hello-mobili' ),                    'slug'  => 'gray',                    'color' => $gray,                ),                array(                    'name'  => esc_html__( 'Green', 'hello-mobili' ),                    'slug'  => 'green',                    'color' => $green,                ),                array(                    'name'  => esc_html__( 'Blue', 'hello-mobili' ),                    'slug'  => 'blue',                    'color' => $blue,                ),                array(                    'name'  => esc_html__( 'Purple', 'hello-mobili' ),                    'slug'  => 'purple',                    'color' => $purple,                ),                array(                    'name'  => esc_html__( 'Red', 'hello-mobili' ),                    'slug'  => 'red',                    'color' => $red,                ),                array(                    'name'  => esc_html__( 'Orange', 'hello-mobili' ),                    'slug'  => 'orange',                    'color' => $orange,                ),                array(                    'name'  => esc_html__( 'Yellow', 'hello-mobili' ),                    'slug'  => 'yellow',                    'color' => $yellow,                ),                array(                    'name'  => esc_html__( 'White', 'hello-mobili' ),                    'slug'  => 'white',                    'color' => $white,                ),            )        );        // Add support for responsive embedded content.        add_theme_support( 'responsive-embeds' );        // Add support for custom line height controls.        add_theme_support( 'custom-line-height' );        // Add support for experimental link color control.        add_theme_support( 'experimental-link-color' );        // Add support for experimental cover block spacing.        add_theme_support( 'custom-spacing' );        // Add support for custom units.        // This was removed in WordPress 5.6 but is still required to properly support WP 5.5.        add_theme_support( 'custom-units' );        // Remove feed icon link from legacy RSS widget.        add_filter( 'rss_widget_feed_link', '__return_false' );    }}add_action( 'after_setup_theme', 'hello_mobili_one_setup' );